 <?php $__env->startSection('content'); ?> <?php $lockedflag = false; $redirectFlag = false; ?> <?php if(Input::get('loggedOut')): ?> <?php if(App\User::where('username', '=', Crypt::decrypt(Input::get('loggedOut')))->exists()): ?> <?php $lastusername = Crypt::decrypt(Input::get('loggedOut')); $lastuserid = App\User::where('username', '=', $lastusername )->value('id'); $lastuserObj = App\User::with('profile_info')->find($lastuserid); $lastUserNiceName = $lastuserObj->name; $lockedflag = true; ?> <?php endif; ?> <?php endif; ?> <?php if(Input::get('redirect')): ?> <?php $redirectPath = array_get(parse_url(Request::get('redirect')),'path'); $redirectFlag = true; ?> <?php endif; ?> <form class="form-horizontal" method="POST" action="<?php echo e(route('login')); ?>"> <?php echo e(csrf_field()); ?> <div class="panel panel-body login-form"> <?php if($redirectFlag==true): ?> <input id="redirectPath" type="hidden" name="redirectPath" value="<?php echo e($redirectPath); ?>"> <?php endif; ?> <?php if($lockedflag==true): ?> <input id="username" type="hidden" name="username" value="<?php echo e($lastusername); ?>" > <div class="thumb thumb-rounded"> <?php echo e(Html::image(route('imagecache', ['template' => 'profile', 'filename' => $lastuserObj->profile_info->image]), 'a picture', array(''))); ?> </div> <h6 class="content-group text-center text-semibold no-margin-top"><?php echo e($lastUserNiceName); ?> <small class="display-block"><?php echo e(trans('all.unlock_your_account')); ?></small></h6> <?php endif; ?> <?php if($lockedflag==false): ?> <div class="text-center"> <div class="icon-object border-slate-300 text-slate-300"><img src="<?php echo e(url('img/cache/logo/logo.jpg')); ?>" alt="solidus"></div> <h5 class="content-group">Login to your account <small class="display-block">Enter your credentials below</small></h5> </div> <div class="form-group<?php echo e($errors->has('username') ? ' has-error' : ''); ?> has-feedback has-feedback-left" > <input id="username" type="text" class="form-control" name="username" value="<?php echo e(old('username')); ?>" required autofocus placeholder="username" autofocus="true"> <?php if($errors->has('username')): ?> <span class="help-block"> <strong><?php echo e($errors->first('username')); ?></strong> </span> <?php endif; ?> <div class="form-control-feedback"> <i class="icon-user text-muted"></i> </div> </div> <?php endif; ?> <div class="form-group<?php echo e($errors->has('password') ? ' has-error' : ''); ?> has-feedback has-feedback-left"> <input id="password" type="password" placeholder="Password" class="form-control" name="password" required <?php if($lockedflag == true): ?> autofocus <?php endif; ?> > <?php if($errors->has('password')): ?> <span class="help-block"> <strong><?php echo e($errors->first('password')); ?></strong> </span> <?php endif; ?> <div class="form-control-feedback"> <i class="icon-lock2 text-muted"></i> </div> </div> <div class="form-group"> <div class="checkbox"> <label> <input type="checkbox" class="styled" name="remember" <?php echo e(old('remember') ? 'checked' : ''); ?>> Remember Me </label> </div> </div> <?php if($lockedflag==true): ?> <div class="form-group"> <button type="submit" class="btn btn-primary btn-block">Unlock account<i class="icon-circle-right2 position-right"></i></button> </div> <?php endif; ?> <?php if($lockedflag==false): ?> <div class="form-group"> <button type="submit" class="btn btn-primary btn-block">Sign in <i class="icon-circle-right2 position-right"></i></button> </div> <?php endif; ?> <div class="text-center"> <a class="btn btn-link" href="<?php echo e(route('password.request')); ?>"> Forgot Your Password? </a> </div> </div> </form> <?php $__env->stopSection(); ?>
